const BG_COLORS = [
  'rgba(255, 0, 0, 0.3)',
  'rgba(255, 255, 0, 0.3)',
  'rgba(255, 0, 255, 0.3)',
  'rgba(0, 255, 255, 0.3)',
  'rgba(0, 255, 0, 0.3)',
  'rgba(0, 0, 255, 0.3)',
]

export const App: BlockComponent<AppProps> = ({ emojiName }) => {
  const packedData = DATA.emojis[emojiName]
  if (!packedData) {
    console.warn("No emoji with the name", emojiName)
    return null
  }

  const [emoji, descriptionOrEmpty, categoryIndex] = packedData
  const category = DATA.categories[categoryIndex]

  const displayName = emojiName.split('_').map(word => `${word[0].toUpperCase()}${word.slice(1)}`).join(' ')

  // If description is omitted, that means it's the same as the name of the emoji -- transform it to make it look nicer
  const description = descriptionOrEmpty || `${emojiName[0].toUpperCase()}${emojiName.replace(/_/g, ' ').toLowerCase().slice(1)}`

  // Pick a background color based on the character codes that make up the emoji
  let charCodeSum = 0
  for (let i = 0; i < emoji.length; i++) {
    charCodeSum = (charCodeSum * 65536) + emoji.charCodeAt(i)
  }
  const backgroundColor = BG_COLORS[charCodeSum % BG_COLORS.length]

  // Repeat the display name enough times to use as a background
  let nameRepeated = ''
  while (nameRepeated.length < 200) {
    nameRepeated += displayName
  }

  return <div className="--shinypb-etc">
    <div className="--shinypb-etc-emoji-container" style={{ backgroundColor }}>
      <span className="--shinypb-etc-emoji">{emoji}</span>
      <span className="--shinypb-etc-emoji-bg">{nameRepeated}</span>
    </div>
    <div className="--shinypb-etc-info">
      <span className="--shinypb-etc-name">{displayName}</span>
      <span className="--shinypb-etc-desc">“{description}”</span>
      <span className="--shinypb-etc-cat">{category}</span>
    </div>
    <style>{styles}</style>
  </div>
}
